import mongoose from "mongoose";

const app = express();

app.use(express.json());
const port = 8000;

mongoose
  .connect("mongodb://127.0.0.1:27017/finalProjectData")
  .then(() => console.log("DB Connected!"));

app.listen(port, () => {
  console.log("Server is running on port " + port);
});

