sourcesContent:
import departmentModel from "../Model/department.model";
import path from "path";
import fs from "fs";
import multer from "multer";

const UPLOAD_DIR = "./uploads";
if (!fs.existsSync(UPLOAD_DIR)) {
  fs.mkdirSync(UPLOAD_DIR, { recursive: true });
}

const storage = multer.diskStorage({
  destination: (req, file, cb) => cb(null, UPLOAD_DIR),
  filename: (req, file, cb) => {
    const uniqueFilename = `${
      path.parse(file.originalname).name
    }-${Date.now()}${path.extname(file.originalname)}`;
    cb(null, uniqueFilename);
  },
});

const upload = multer({ storage: storage }).fields([
  { name: "thumbnail", maxCount: 1 },
]);

export const addDepartment = async (req, res) => {
  upload(req, res, async (err) => {
    if (err)
      return res.status(400).json({ message: err.message, success: false });

    try {
      const img = req.files?.thumbnail?.[0]?.filename || null;
      const { DepartmentName, Description } = req.body;

      if (!DepartmentName || !Description) {
        return res
          .status(400)
          .json({ message: "All fields are required", success: false });
      }

      const created = await departmentModel.create({
        thumbnail: img,
        DepartmentName,
        Description,
      });

      return res.status(201).json({
        data: created,
        message: "Department created successfully",
        success: true,
      });
    } catch (err) {
      return res.status(500).json({ message: err.message, success: false });
    }
  });
};

export const getDepartments = async (req, res) => {
  try {
    const departments = await departmentModel.find();
    return res.status(200).json({
      data: departments,
      filepath: "http://localhost:8000/static",
      message: "Departments fetched successfully",
      success: true,
    });
  } catch (err) {
    return res.status(500).json({ message: err.message, success: false });
  }
};

export const getDepartment = async (req, res) => {
  try {
    const { department_id } = req.params;
    const department = await departmentModel.findById(department_id);

    if (!department) {
      return res
        .status(404)
        .json({ message: "Department not found", success: false });
    }

    return res.status(200).json({
      data: department,
      message: "Department fetched successfully",
      success: true,
    });
  } catch (err) {
    return res.status(500).json({ message: err.message, success: false });
  }
};

export const updateDepartment = async (req, res) => {
  upload(req, res, async (err) => {
    if (err)
      return res.status(400).json({ message: err.message, success: false });

    try {
      const { department_id } = req.params;
      const { DepartmentName, Description } = req.body;
      const img = req.files?.thumbnail?.[0]?.filename || null;

      const existingDepartment = await departmentModel.findById(department_id);
      if (!existingDepartment) {
        return res
          .status(404)
          .json({ message: "Department not found", success: false });
      }

      if (img && existingDepartment.thumbnail) {
        const oldImagePath = path.join(
          UPLOAD_DIR,
          existingDepartment.thumbnail
        );
        if (fs.existsSync(oldImagePath)) fs.unlinkSync(oldImagePath);
      }

      const updatedDepartment = await departmentModel.findByIdAndUpdate(
        department_id,
        {
          $set: { DepartmentName, Description, ...(img && { thumbnail: img }) },
        },
        { new: true }
      );

      return res.status(200).json({
        data: updatedDepartment,
        message: "Department updated successfully!",
        success: true,
      });
    } catch (err) {
      return res.status(500).json({ message: err.message, success: false });
    }
  });
};

export const deleteDepartment = async (req, res) => {
  try {
    const { department_id } = req.params;
    const department = await departmentModel.findByIdAndDelete(department_id);

    if (!department) {
      return res
        .status(404)
        .json({ message: "Department not found", success: false });
    }

    if (department.thumbnail) {
      const imagePath = path.join(UPLOAD_DIR, department.thumbnail);
      if (fs.existsSync(imagePath)) fs.unlinkSync(imagePath);
    }

    return res
      .status(200)
      .json({ message: "Department deleted successfully", success: true });
  } catch (err) {
    return res.status(500).json({ message: err.message, success: false });
  }
};

